// Extraer datos del mensaje entrante
const input = $input.first().json;

const employeeId = input.employeeId;
const sessionId = input.sessionId;
const message = input.message;
const phoneNumber = input.phoneNumber;
const context = input.context || {};

// Construir el prompt del sistema
const systemPrompt = `Eres un asistente de Recursos Humanos amigable y profesional para una empresa mexicana.
Tu nombre es "Asistente RRHH".

Puedes ayudar a los empleados con:
1. Consultas de vacaciones y saldo de días
2. Solicitudes de permisos
3. Consultas de nómina y recibos
4. Registro de asistencia
5. Preguntas generales sobre políticas de la empresa

Contexto del empleado:
- ID: ${employeeId}
- Días de vacaciones disponibles: ${context.vacationBalance || 'No disponible'}
- Último checado: ${context.lastAttendance || 'No disponible'}

Responde siempre en español de manera clara y concisa.
Si necesitas información que no tienes, indica amablemente que el empleado debe contactar a RH directamente.
No inventes información sobre políticas o saldos.`;

return {
  json: {
    employeeId,
    sessionId,
    phoneNumber,
    userMessage: message,
    systemPrompt,
    context
  }
};

// Procesar la respuesta de Claude
const input = $input.first().json;
const prevData = $('Preparar Contexto').first().json;

// Extraer la respuesta de Claude
const aiResponse = input.message?.content || input.text || 'Lo siento, hubo un error procesando tu mensaje.';

// Detectar intención básica de la respuesta
let intent = 'general';
const lowerMessage = prevData.userMessage.toLowerCase();

if (lowerMessage.includes('vacacion') || lowerMessage.includes('días')) {
  intent = 'vacation_query';
} else if (lowerMessage.includes('permiso')) {
  intent = 'permission_request';
} else if (lowerMessage.includes('nómina') || lowerMessage.includes('sueldo') || lowerMessage.includes('pago')) {
  intent = 'payroll_query';
} else if (lowerMessage.includes('entrada') || lowerMessage.includes('salida') || lowerMessage.includes('checado')) {
  intent = 'attendance';
}

return {
  json: {
    response: aiResponse,
    intent: intent,
    confidence: 0.85,
    sessionId: prevData.sessionId,
    employeeId: prevData.employeeId,
    phoneNumber: prevData.phoneNumber,
    suggestions: getSuggestions(intent)
  }
};

function getSuggestions(intent) {
  const suggestions = {
    vacation_query: ['Solicitar vacaciones', 'Ver historial', 'Menú principal'],
    permission_request: ['Permiso personal', 'Cita médica', 'Cancelar'],
    payroll_query: ['Descargar PDF', 'Ver CFDI', 'Nóminas anteriores'],
    attendance: ['Ver mi horario', 'Historial de asistencia', 'Menú principal'],
    general: ['Vacaciones', 'Nómina', 'Permisos', 'Ayuda']
  };
  return suggestions[intent] || suggestions.general;
}
